import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-breadcrum',
  templateUrl: './breadcrum.component.html',
  styleUrls: ['./breadcrum.component.scss'],
})
export class BreadcrumComponent implements OnInit {
  @Input() title: string = '';
  @Input() titleButton: string = '';
  @Input() link: string = '';
  constructor() {}

  ngOnInit(): void {}
}
<!-- Renderizar el titulo y un boton para linkiar a otra página -->
<div class="breadcrum">
  <h1 class="breadcrum__title">{{ title }}</h1>
  <button class="breadcrum__action">
    <a [routerLink]="['/' + link]"> Ir a {{ titleButton }}</a>
  </button>
</div>
